package main

import "fmt"

func isUnique(fourCharString string) bool {
	counts := make(map[rune]int)
	for _, char := range fourCharString {
		counts[char]++
	}

	for _, count := range counts {
		if count > 1 {
			return true
		}
	}
	return false
}

func firstTask(taskInput string) int {
	for i, _ := range taskInput {
		packet := taskInput[i : i+14]
		uniqueBool := isUnique(packet)

		if !uniqueBool {
			return i + 14
		}
		if i >= len(taskInput)-14 {
			return 0
		}
	}
	return 0
}

func main() {
	pos := firstTask("")
	fmt.Println(pos)
}
